import { StyleSheet, Text, View, Image, FlatList } from 'react-native';
import React, { useState, useEffect } from 'react';
import { Card, IconButton } from 'react-native-paper';
import Slider from '@react-native-community/slider';
import openMap from 'react-native-open-maps';
import { useReportById } from '../../hooks/useReportById';
import ReportReactionsChart from '../../shared/charts/ReportReactionsChart';
import Layout from '../../shared/layout/Layout';
import LoadingView from '../../shared/loading/LoadingView';

const ReportDetails = ({ route, navigation }) => {
  const { reportId } = route.params;

  const { data, isLoading } = useReportById(reportId);

  if (isLoading) return <LoadingView />;
  return (
    <Layout goBack={true}>
      <Card style={styles.cardStyle}>
        <Text style={styles.title}>{data?.title}</Text>
        <Text style={styles.description}>{data?.description}</Text>
        <Text
          style={styles.description}
        >{`reagerade anv√§ndare ${data?.reportReactions?.length}`}</Text>
        <View style={styles.imagesContainer}>
          <FlatList
            data={data?.images}
            keyExtractor={({ item }) => item}
            horizontal={true}
            showsHorizontalScrollIndicator={false}
            renderItem={({ item }) => (
              <Image source={{ uri: item }} style={styles.imageItem} />
            )}
          />
        </View>
        <View></View>
      </Card>
    </Layout>
  );
};

export default ReportDetails;

const styles = StyleSheet.create({
  imageItem: {
    width: 300,
    height: 200,
    margin: 10,
  },
  cardStyle: {
    margin: 10,
    padding: 10,
  },
  title: {
    fontSize: 20,
  },
  description: {
    color: '#3c3c3c70',
  },
  imagesContainer: {
    marginTop: 20,
    marginBottom: 20,
  },
  mapLink: {
    position: 'absolute',
    top: -10,
    right: -10,
    zIndex: 99,
  },
});
