import { StyleSheet, Text, View, Dimensions } from 'react-native';
import React from 'react';
import { PieChart } from 'react-native-chart-kit';

const ReportReactionsChart = ({ reportReactions }) => {
  const data = {
    labels: [
      'valdigt stämmer ',
      'stämmer',
      'vanligt',
      'stämmer inte',
      'stämmer alls',
    ],
    data: [
      {
        name: 'valdigt stämmer',
        population: reportReactions.filter((c) => c.value >= 0.5).length,
        color: 'rgba(131, 167, 234, 1)',
        legendFontColor: '#7F7F7F',
        legendFontSize: 15,
      },

      {
        name: 'stämmer',
        population: reportReactions.filter((c) => c.value > 0.25 < 0.5).length,
        color: 'rgba(131, 167, 234, 1)',
        legendFontColor: '#7F7F7F',
        legendFontSize: 15,
      },

      {
        name: 'vanligt',
        population: reportReactions.filter((c) => c.value >= 0.5).length,
        color: 'rgba(131, 167, 234, 1)',
        legendFontColor: '#7F7F7F',
        legendFontSize: 15,
      },

      {
        name: 'stämmer alls',
        population: reportReactions.filter((c) => c.value >= 0.5).length,
        color: 'rgba(131, 167, 234, 1)',
        legendFontColor: '#7F7F7F',
        legendFontSize: 15,
      },
      ,
      ,
      reportReactions.filter((c) => c.value > -0.25 && c.value < 0.25),
      reportReactions.filter((c) => c.value < 0.25),
    ],
  };

  const chartConfig = {
    backgroundGradientFrom: '#fff',
    backgroundGradientFromOpacity: 0,
    backgroundGradientTo: '#fff',
    backgroundGradientToOpacity: 0.5,
    color: (opacity = 1) => `rgba(3, 169, 244,${opacity})`,
    strokeWidth: 2, // optional, default 3
    barPercentage: 0.5,
    useShadowColorFromDataset: false, // optional
  };

  return (
    <View>
      <PieChart
        data={data}
        width={Dimensions.get('window').width - 100}
        height={220}
        strokeWidth={8}
        radius={16}
        chartConfig={chartConfig}
        hideLegend={false}
      />
    </View>
  );
};

export default ReportReactionsChart;

const styles = StyleSheet.create({});
