import { StyleSheet, Text, View, Dimensions } from 'react-native';
import React from 'react';
import { ProgressChart } from 'react-native-chart-kit';

const ReportReactionsChart = ({ reportReactions }) => {
  const data = {
    labels: [
      'valdigt stämmer ',
      'stämmer',
      'vanligt',
      'stämmer inte',
      'stämmer alls',
    ],
    data: [
      reportReactions.filter((c) => c.value >= 0.5),
      reportReactions.filter((c) => c.value > 0.25 < 0.5),
      reportReactions.filter((c) => c.value > -0.25 && c.value < 0.25),
      reportReactions.filter((c) => c.value < 0.25),
    ],
  };

  const chartConfig = {
    backgroundGradientFrom: '#fff',
    backgroundGradientFromOpacity: 0,
    backgroundGradientTo: '#fff',
    backgroundGradientToOpacity: 0.5,
    color: (opacity = 1) => `rgba(3, 169, 244,${opacity})`,
    strokeWidth: 2, // optional, default 3
    barPercentage: 0.5,
    useShadowColorFromDataset: false, // optional
  };

  return (
    <View>
      <ProgressChart
        data={data}
        width={Dimensions.get('window').width - 20}
        height={220}
        strokeWidth={16}
        radius={32}
        chartConfig={chartConfig}
        hideLegend={false}
      />
    </View>
  );
};

export default ReportReactionsChart;

const styles = StyleSheet.create({});
